INSERT INTO quote(id, text, PHILOSOPHER_ID) VALUES (10001, 'Happiness is the highest good.', 20001);
INSERT INTO quote(id, text, PHILOSOPHER_ID) VALUES (10002, 'Plato is a friend but Truth is a better friend.', 20001);
INSERT INTO quote(id, text, PHILOSOPHER_ID) VALUES (10003, 'The only thing I know is that I know nothing', 20002);
INSERT INTO quote(id, text, PHILOSOPHER_ID) VALUES (10004, 'Man is the measure of all things.', 20003);
INSERT INTO quote(id, text, PHILOSOPHER_ID) VALUES (10005, 'We are too weak to discover the truth by reason alone', 20004);
INSERT INTO quote(id, text, PHILOSOPHER_ID) VALUES (10006, 'God is dead! He remains dead! And we have killed him.', 20006);
INSERT INTO quote(id, text, PHILOSOPHER_ID) VALUES (10007, 'You can discover more about a person in an hour of play than in a year of conversation.', 20005);
INSERT INTO quote(id, text, PHILOSOPHER_ID) VALUES (10008, 'Happiness is not an ideal of reason but of imagination.', 20007);
INSERT INTO quote(id, text, PHILOSOPHER_ID) VALUES (10009, 'He who thinks great thoughts, often makes great errors.', 20008);
INSERT INTO quote(id, text, PHILOSOPHER_ID) VALUES (10010, 'Cogito ergo sum.', 20009);
INSERT INTO quote(id, text, PHILOSOPHER_ID) VALUES (10011, 'I can control my passions and emotions if I can understand their nature.', 20010);
INSERT INTO quote(id, text, PHILOSOPHER_ID) VALUES (10012, 'Everything that exists is born for no reason, carries on living through weakness, and dies by accident.', 20011);
INSERT INTO quote(id, text, PHILOSOPHER_ID) VALUES (10013, 'Philosophers have hitherto only interpreted the world in various ways; the point, however, is to change it.', 20012);
INSERT INTO quote(id, text, PHILOSOPHER_ID) VALUES (10014, 'Religion is the sign of the oppressed ... it is the opium of the people.', 20012);
